import React, { useState, useEffect, useRef, useCallback } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Bar } from 'react-chartjs-2';
import './App.css';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [isConnected, setIsConnected] = useState(false);
  const [isRecording, setIsRecording] = useState(false);
  const [speakerData, setSpeakerData] = useState([]);
  const [meetingDuration, setMeetingDuration] = useState(0);
  const [totalSpeakingTime, setTotalSpeakingTime] = useState(0);
  const [silenceTime, setSilenceTime] = useState(0);
  const [prompts, setPrompts] = useState([]);
  const [agenda, setAgenda] = useState('');
  const [agendaItems, setAgendaItems] = useState([]);

  // Enhanced real-time states
  const [currentSpeaker, setCurrentSpeaker] = useState(null);
  const [transcriptions, setTranscriptions] = useState([]);
  const [lastActivity, setLastActivity] = useState(null);
  const [speakerActivity, setSpeakerActivity] = useState({});
  const [realtimeStats, setRealtimeStats] = useState({
    meeting_duration: 0,
    total_speaking_time: 0,
    silence_time: 0
  });

  // Connection and performance states
  const [connectionQuality, setConnectionQuality] = useState('good');
  const [lastMessageTime, setLastMessageTime] = useState(Date.now());
  const [messageCount, setMessageCount] = useState(0);

  const ws = useRef(null);
  const reconnectTimeoutRef = useRef(null);
  const isConnectingRef = useRef(false);
  const transcriptionRef = useRef(null);
  const performanceTimerRef = useRef(null);
  const backendUrl = process.env.REACT_APP_BACKEND_URL || 'http://localhost:8001';

  // Performance monitoring
  useEffect(() => {
    performanceTimerRef.current = setInterval(() => {
      const now = Date.now();
      const timeSinceLastMessage = now - lastMessageTime;

      if (isRecording && timeSinceLastMessage > 5000) {
        setConnectionQuality('poor');
      } else if (isRecording && timeSinceLastMessage > 2000) {
        setConnectionQuality('fair');
      } else {
        setConnectionQuality('good');
      }
    }, 1000);

    return () => {
      if (performanceTimerRef.current) {
        clearInterval(performanceTimerRef.current);
      }
    };
  }, [lastMessageTime, isRecording]);

  useEffect(() => {
    connectToWebSocket();

    return () => {
      isConnectingRef.current = false;
      if (reconnectTimeoutRef.current) {
        clearTimeout(reconnectTimeoutRef.current);
        reconnectTimeoutRef.current = null;
      }
      if (performanceTimerRef.current) {
        clearInterval(performanceTimerRef.current);
      }
      if (ws.current) {
        ws.current.onopen = null;
        ws.current.onmessage = null;
        ws.current.onclose = null;
        ws.current.onerror = null;

        if (ws.current.readyState === WebSocket.OPEN || ws.current.readyState === WebSocket.CONNECTING) {
          ws.current.close();
        }
        ws.current = null;
      }
    };
  }, []);

  // Auto-scroll transcriptions
  useEffect(() => {
    if (transcriptionRef.current) {
      transcriptionRef.current.scrollTop = transcriptionRef.current.scrollHeight;
    }
  }, [transcriptions]);

  const connectToWebSocket = useCallback(() => {
    if (isConnectingRef.current || (ws.current && ws.current.readyState === WebSocket.CONNECTING)) {
      return;
    }

    if (ws.current) {
      ws.current.onopen = null;
      ws.current.onmessage = null;
      ws.current.onclose = null;
      ws.current.onerror = null;
      if (ws.current.readyState === WebSocket.OPEN) {
        ws.current.close();
      }
      ws.current = null;
    }

    const wsUrl = backendUrl.replace('http', 'ws') + '/api/ws';
    isConnectingRef.current = true;

    try {
      console.log('Connecting to WebSocket:', wsUrl);
      ws.current = new WebSocket(wsUrl);

      ws.current.onopen = () => {
        console.log('Connected to WebSocket');
        isConnectingRef.current = false;
        setIsConnected(true);
        setConnectionQuality('good');

        if (ws.current && ws.current.readyState === WebSocket.OPEN) {
          ws.current.send(JSON.stringify({ type: 'ping' }));
        }
      };

      ws.current.onmessage = (event) => {
        try {
          const data = JSON.parse(event.data);
          setLastMessageTime(Date.now());
          setMessageCount(prev => prev + 1);
          handleWebSocketMessage(data);
        } catch (error) {
          console.error('Error parsing WebSocket message:', error);
        }
      };

      ws.current.onclose = (event) => {
        console.log('WebSocket connection closed', event.code, event.reason);
        isConnectingRef.current = false;
        setIsConnected(false);
        setConnectionQuality('poor');

        if (event.code !== 1000 && event.code !== 1001) {
          if (reconnectTimeoutRef.current) {
            clearTimeout(reconnectTimeoutRef.current);
          }

          reconnectTimeoutRef.current = setTimeout(() => {
            connectToWebSocket();
          }, 3000);
        }
      };

      ws.current.onerror = (error) => {
        console.error('WebSocket error:', error);
        isConnectingRef.current = false;
        setIsConnected(false);
        setConnectionQuality('poor');
      };

    } catch (error) {
      console.error('Failed to create WebSocket connection:', error);
      isConnectingRef.current = false;
      setIsConnected(false);
      setConnectionQuality('poor');

      if (reconnectTimeoutRef.current) {
        clearTimeout(reconnectTimeoutRef.current);
      }
      reconnectTimeoutRef.current = setTimeout(() => {
        connectToWebSocket();
      }, 3000);
    }
  }, [backendUrl]);

  const handleWebSocketMessage = useCallback((data) => {
    switch (data.type) {
      case 'speaker_activity':
        handleSpeakerActivity(data);
        break;

      case 'speaker_stats':
        handleSpeakerStats(data);
        break;

      case 'transcription':
        handleTranscription(data);
        break;

      case 'timer_update':
        handleTimerUpdate(data);
        break;

      case 'prompt':
        setPrompts(prev => [...prev, data]);
        break;

      default:
        console.log('Unknown message type:', data.type, data);
    }
  }, []);

  const handleSpeakerActivity = useCallback((data) => {
    const { speaker_id, action, timestamp } = data;

    setLastActivity({ speaker_id, action, timestamp });

    // Update speaker activity tracking
    setSpeakerActivity(prev => ({
      ...prev,
      [speaker_id]: {
        ...prev[speaker_id],
        last_action: action,
        last_timestamp: timestamp,
        is_active: action === 'started'
      }
    }));

    if (action === 'started') {
      setCurrentSpeaker(speaker_id);
    } else if (action === 'stopped' && currentSpeaker === speaker_id) {
      setCurrentSpeaker(null);
    }
  }, [currentSpeaker]);

  const handleSpeakerStats = useCallback((data) => {
    const { speakers, meeting_duration, total_speaking_time, silence_time } = data;

    // Convert to the format expected by the chart
    const formattedSpeakers = speakers.map(speaker => ({
      speaker_id: speaker.speaker_id,
      talk_time_in_seconds: speaker.talk_time_seconds,
      percentage: speaker.percentage,
      is_speaking: speaker.is_speaking,
      speech_segments: speaker.speech_segments || 0
    }));

    setSpeakerData(formattedSpeakers);
    setMeetingDuration(meeting_duration);
    setTotalSpeakingTime(total_speaking_time);
    setSilenceTime(silence_time);

    // Update realtime stats
    setRealtimeStats({
      meeting_duration,
      total_speaking_time,
      silence_time
    });
  }, []);

  const handleTranscription = useCallback((data) => {
    const { speaker_id, text, timestamp } = data;

    setTranscriptions(prev => {
      const newTranscription = {
        id: `${timestamp}-${Math.random()}`,
        speaker_id,
        text,
        timestamp,
        time: new Date(timestamp * 1000).toLocaleTimeString(),
        confidence: data.confidence || 1.0 // If confidence is provided by server
      };

      // Keep only last 100 transcriptions for performance
      const updated = [...prev, newTranscription];
      return updated.slice(-100);
    });
  }, []);

  const handleTimerUpdate = useCallback((data) => {
    const { meeting_duration } = data;
    setMeetingDuration(meeting_duration);
  }, []);

  const startMeeting = async () => {
    try {
      const response = await fetch(`${backendUrl}/api/meeting/start`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        setIsRecording(true);
        setPrompts([]);
        setSpeakerData([]);
        setMeetingDuration(0);
        setTotalSpeakingTime(0);
        setSilenceTime(0);
        setTranscriptions([]);
        setCurrentSpeaker(null);
        setLastActivity(null);
        setSpeakerActivity({});
        setRealtimeStats({ meeting_duration: 0, total_speaking_time: 0, silence_time: 0 });
        setMessageCount(0);
        console.log('Meeting started successfully');
      } else {
        const errorData = await response.json().catch(() => ({ message: 'Unknown error' }));
        console.error('Failed to start meeting:', errorData);
        alert(`Failed to start meeting: ${errorData.message || 'Please check if the backend is running.'}`);
      }
    } catch (error) {
      console.error('Error starting meeting:', error);
      alert('Error starting meeting. Please check your connection and ensure the backend server is running.');
    }
  };

  const stopMeeting = async () => {
    try {
      const response = await fetch(`${backendUrl}/api/meeting/stop`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        setIsRecording(false);
        setCurrentSpeaker(null);
        setSpeakerActivity({});
        console.log('Meeting stopped successfully');
      } else {
        console.error('Failed to stop meeting');
      }
    } catch (error) {
      console.error('Error stopping meeting:', error);
    }
  };

  const setMeetingAgenda = async () => {
    if (!agenda.trim()) return;

    const items = agenda.split('\n').filter(item => item.trim()).map(item => item.trim());

    try {
      const response = await fetch(`${backendUrl}/api/meeting/agenda`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ items }),
      });

      if (response.ok) {
        setAgendaItems(items);
        console.log('Agenda set successfully');
      } else {
        console.error('Failed to set agenda');
      }
    } catch (error) {
      console.error('Error setting agenda:', error);
    }
  };

  const dismissPrompt = (index) => {
    setPrompts(prev => prev.filter((_, i) => i !== index));
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = Math.floor(seconds % 60);
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const formatTimeDetailed = (seconds) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = Math.floor(seconds % 60);

    if (hours > 0) {
      return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
    }
    return `${minutes}:${secs.toString().padStart(2, '0')}`;
  };

  const getConnectionStatusIcon = () => {
    switch (connectionQuality) {
      case 'good': return 'ðŸŸ¢';
      case 'fair': return 'ðŸŸ¡';
      case 'poor': return 'ðŸ”´';
      default: return 'âš«';
    }
  };

  const getConnectionStatusText = () => {
    const base = isConnected ? 'Connected' : 'Disconnected';
    if (isConnected && isRecording) {
      return `${base} (${connectionQuality})`;
    }
    return base;
  };

  // Enhanced chart data with better visual feedback
  const chartData = {
    labels: speakerData.map(speaker => speaker.speaker_id),
    datasets: [
      {
        label: 'Speaking Time (seconds)',
        data: speakerData.map(speaker => speaker.talk_time_in_seconds),
        backgroundColor: speakerData.map((speaker, index) => {
          const colors = ['#3B82F6', '#10B981', '#F59E0B', '#EF4444', '#8B5CF6', '#EC4899', '#06B6D4'];
          const baseColor = colors[index % colors.length];
          return speaker.is_speaking ? baseColor : baseColor + '80';
        }),
        borderColor: speakerData.map((speaker, index) => {
          const colors = ['#2563EB', '#059669', '#D97706', '#DC2626', '#7C3AED', '#DB2777', '#0891B2'];
          return colors[index % colors.length];
        }),
        borderWidth: speakerData.map(speaker => speaker.is_speaking ? 3 : 1),
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Real-Time Speaking Time Analysis',
        font: {
          size: 18,
        },
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Time (seconds)',
        },
      },
      x: {
        title: {
          display: true,
          text: 'Speakers',
        },
      },
    },
    animation: {
      duration: 300,
    },
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>ðŸŽ¤ Real-Time Meeting Assistant</h1>
        <div className="header-info">
          <div className="connection-status">
            <span className={`status-indicator ${isConnected ? 'connected' : 'disconnected'} ${connectionQuality}`}>
              {getConnectionStatusIcon()} {getConnectionStatusText()}
            </span>
          </div>
          {isRecording && (
            <div className="performance-stats">
              <span className="message-count">Messages: {messageCount}</span>
            </div>
          )}
        </div>
      </header>

      <main className="main-content">
        {/* Enhanced Control Panel */}
        <div className="control-panel">
          <div className="meeting-controls">
            <button
              onClick={isRecording ? stopMeeting : startMeeting}
              className={`control-button ${isRecording ? 'stop' : 'start'}`}
              disabled={!isConnected}
            >
              {isRecording ? 'â¹ï¸ Stop Meeting' : 'â–¶ï¸ Start Meeting'}
            </button>

            <div className="meeting-stats">
              <div className="stat">
                <span className="stat-label">Duration:</span>
                <span className="stat-value">{formatTimeDetailed(meetingDuration)}</span>
              </div>
              <div className="stat">
                <span className="stat-label">Speaking:</span>
                <span className="stat-value">{formatTimeDetailed(totalSpeakingTime)}</span>
              </div>
              <div className="stat">
                <span className="stat-label">Silence:</span>
                <span className="stat-value">{formatTimeDetailed(silenceTime)}</span>
              </div>
            </div>
          </div>

          {/* Enhanced Real-time Speaker Status */}
          {isRecording && (
            <div className="current-speaker-status">
              <h3>ðŸŽ™ï¸ Live Audio Status</h3>
              <div className={`speaker-indicator ${currentSpeaker ? 'active' : 'inactive'}`}>
                {currentSpeaker ? (
                  <div className="speaking-animation">
                    <div className="audio-visualizer">
                      <div className="bar"></div>
                      <div className="bar"></div>
                      <div className="bar"></div>
                      <div className="bar"></div>
                    </div>
                    <div className="speaker-info">
                      <span className="speaker-text">{currentSpeaker}</span>
                      <span className="status-text">is speaking...</span>
                    </div>
                  </div>
                ) : (
                  <div className="listening-state">
                    <div className="listening-icon">ðŸ‘‚</div>
                    <span className="inactive-text">Listening for speech...</span>
                  </div>
                )}
              </div>

              {/* Speaker activity history */}
              {Object.keys(speakerActivity).length > 0 && (
                <div className="speaker-activity-history">
                  <h4>Recent Activity</h4>
                  <div className="activity-list">
                    {Object.entries(speakerActivity)
                      .sort(([,a], [,b]) => (b.last_timestamp || 0) - (a.last_timestamp || 0))
                      .slice(0, 3)
                      .map(([speakerId, activity]) => (
                        <div key={speakerId} className={`activity-item ${activity.is_active ? 'active' : 'inactive'}`}>
                          <span className="speaker-name">{speakerId}</span>
                          <span className="activity-status">
                            {activity.is_active ? 'Speaking' : 'Silent'}
                          </span>
                          <span className="activity-time">
                            {new Date((activity.last_timestamp || 0) * 1000).toLocaleTimeString()}
                          </span>
                        </div>
                      ))}
                  </div>
                </div>
              )}
            </div>
          )}

          <div className="agenda-section">
            <h3>Meeting Agenda</h3>
            <textarea
              value={agenda}
              onChange={(e) => setAgenda(e.target.value)}
              placeholder="Enter agenda items (one per line)"
              className="agenda-input"
              rows="4"
            />
            <button onClick={setMeetingAgenda} className="set-agenda-button">
              Set Agenda
            </button>
            {agendaItems.length > 0 && (
              <div className="agenda-items">
                <h4>Current Agenda:</h4>
                <ul>
                  {agendaItems.map((item, index) => (
                    <li key={index}>{item}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        </div>

        {/* Enhanced Real-time Transcription */}
        {isRecording && (
          <div className="transcription-section">
            <div className="transcription-header">
              <h3>ðŸ“ Live Transcription</h3>
              <div className="transcription-stats">
                <span className="transcript-count">{transcriptions.length} messages</span>
                {transcriptions.length > 0 && (
                  <button 
                    className="clear-transcripts"
                    onClick={() => setTranscriptions([])}
                  >
                    Clear
                  </button>
                )}
              </div>
            </div>
            <div className="transcription-container" ref={transcriptionRef}>
              {transcriptions.length === 0 ? (
                <div className="no-transcription">
                  <div className="waiting-indicator">
                    <div className="pulse-ring"></div>
                    <span>Waiting for speech...</span>
                  </div>
                </div>
              ) : (
                transcriptions.map((transcription) => (
                  <div key={transcription.id} className="transcription-item">
                    <div className="transcription-header-item">
                      <span className="speaker-name">{transcription.speaker_id}</span>
                      <div className="transcription-meta">
                        <span className="transcription-time">{transcription.time}</span>
                        {transcription.confidence && (
                          <span className="confidence-score">
                            {Math.round(transcription.confidence * 100)}%
                          </span>
                        )}
                      </div>
                    </div>
                    <div className="transcription-text">{transcription.text}</div>
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {/* Prompts Section */}
        {prompts.length > 0 && (
          <div className="prompts-section">
            <h3>ðŸ“‹ Facilitator Prompts</h3>
            {prompts.map((prompt, index) => (
              <div key={index} className={`prompt ${prompt.prompt_type}`}>
                <div className="prompt-content">
                  <strong>{prompt.prompt_type.replace('_', ' ').toUpperCase()}:</strong>
                  <p>{prompt.message}</p>
                </div>
                <button
                  onClick={() => dismissPrompt(index)}
                  className="dismiss-button"
                >
                  âœ•
                </button>
              </div>
            ))}
          </div>
        )}

        {/* Enhanced Speaking Time Visualization */}
        <div className="chart-section">
          <div className="chart-container">
            {speakerData.length > 0 ? (
              <div className="chart-with-stats">
                <div className="chart-wrapper">
                  <Bar data={chartData} options={chartOptions} />
                </div>
                <div className="speaker-stats fade-in">
                  <h3>ðŸ“Š Real-time Speaker Analytics</h3>
                  <div className="stats-grid">
                    {speakerData.map((speaker, index) => (
                      <div 
                        key={speaker.speaker_id} 
                        className={`speaker-stat ${speaker.is_speaking ? 'currently-speaking' : ''}`}
                        style={{
                          animationDelay: `${index * 0.1}s`
                        }}
                      >
                        <div className="speaker-info">
                          <div className="speaker-name-container">
                            <span className="speaker-name">
                              {speaker.speaker_id}
                              {speaker.is_speaking && (
                                <span className="speaking-badge animate-pulse">
                                  ðŸŽ¤ Live
                                </span>
                              )}
                            </span>
                            {speaker.speech_segments > 0 && (
                              <span className="segment-count">
                                {speaker.speech_segments} segments
                              </span>
                            )}
                          </div>
                          <div className="speaker-metrics">
                            <span className="speaker-time">
                              {formatTimeDetailed(speaker.talk_time_in_seconds)}
                            </span>
                            <span className="speaker-percentage">
                              {speaker.percentage.toFixed(1)}%
                            </span>
                          </div>
                        </div>
                        <div className="progress-bar">
                          <div
                            className={`progress-fill ${speaker.is_speaking ? 'active-speaker' : ''}`}
                            style={{
                              width: `${Math.max(speaker.percentage, 2)}%`,
                              backgroundColor: chartData.datasets[0].backgroundColor[index],
                            }}
                          />
                        </div>
                      </div>
                    ))}
                  </div>

                  {/* Meeting summary stats */}
                  <div className="meeting-summary">
                    <div className="summary-stat">
                      <span className="summary-label">Active Speaking</span>
                      <span className="summary-value">
                        {((totalSpeakingTime / Math.max(meetingDuration, 1)) * 100).toFixed(1)}%
                      </span>
                    </div>
                    <div className="summary-stat">
                      <span className="summary-label">Silence</span>
                      <span className="summary-value">
                        {((silenceTime / Math.max(meetingDuration, 1)) * 100).toFixed(1)}%
                      </span>
                    </div>
                    <div className="summary-stat">
                      <span className="summary-label">Participants</span>
                      <span className="summary-value">{speakerData.length}</span>
                    </div>
                  </div>
                </div>
              </div>
            ) : (
              <div className="no-data">
                <h3>ðŸŽ¯ Ready for Real-time Analysis</h3>
                <p>
                  {isRecording
                    ? 'Advanced voice detection is active. Start speaking to see instant analysis with improved speaker identification!'
                    : 'Click "Start Meeting" to begin enhanced real-time speech analysis with voice embeddings and better accuracy.'}
                </p>
                {!isRecording && (
                  <div className="feature-preview">
                    <div className="preview-item">âš¡ Instant voice activity detection</div>
                    <div className="preview-item">ðŸ§  AI-powered speaker identification</div>
                    <div className="preview-item">ðŸ“Š Real-time participation metrics</div>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        {/* Enhanced Instructions */}
        <div className="instructions">
          <h3>ðŸ“– Enhanced Real-time Features</h3>
          <div className="feature-list">
            <div className="feature enhanced">
              <span className="feature-icon">âš¡</span>
              <div className="feature-content">
                <strong>Instant Voice Detection</strong>
                <span>Advanced VAD with 30ms frame processing for immediate response</span>
              </div>
            </div>
            <div className="feature enhanced">
              <span className="feature-icon">ðŸ§ </span>
              <div className="feature-content">
                <strong>AI Speaker Identification</strong>
                <span>Voice embeddings using MFCC features for accurate speaker recognition</span>
              </div>
            </div>
            <div className="feature enhanced">
              <span className="feature-icon">ðŸ“Š</span>
              <div className="feature-content">
                <strong>Real-time Analytics</strong>
                <span>Live participation metrics with 100ms update intervals</span>
              </div>
            </div>
            <div className="feature enhanced">
              <span className="feature-icon">ðŸŽ¤</span>
              <div className="feature-content">
                <strong>Enhanced Transcription</strong>
                <span>Improved Whisper integration with confidence scoring</span>
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
