import React, { useState, useEffect, useRef } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Bar } from 'react-chartjs-2';
import './App.css';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [isConnected, setIsConnected] = useState(false);
  const [isRecording, setIsRecording] = useState(false);
  const [speakerData, setSpeakerData] = useState([]);
  const [meetingDuration, setMeetingDuration] = useState(0);
  const [prompts, setPrompts] = useState([]);
  const [agenda, setAgenda] = useState('');
  const [agendaItems, setAgendaItems] = useState([]);
  const ws = useRef(null);
  const backendUrl = process.env.REACT_APP_BACKEND_URL || 'http://localhost:8001';

  useEffect(() => {
    connectToWebSocket();
    return () => {
      if (ws.current) {
        ws.current.close();
      }
    };
  }, []);

  const connectToWebSocket = () => {
    const wsUrl = backendUrl.replace('http', 'ws') + '/api/ws';

    try {
      ws.current = new WebSocket(wsUrl);

      ws.current.onopen = () => {
        console.log('Connected to WebSocket');
        setIsConnected(true);
        // Send a ping to keep connection alive
        ws.current.send(JSON.stringify({ type: 'ping' }));
      };

      ws.current.onmessage = (event) => {
        const data = JSON.parse(event.data);

        if (data.type === 'speaker_update') {
          setSpeakerData(data.data);
          setMeetingDuration(data.meeting_duration);
        } else if (data.type === 'prompt') {
          setPrompts(prev => [...prev, data]);
        }
      };

      ws.current.onclose = () => {
        console.log('WebSocket connection closed');
        setIsConnected(false);
        // Attempt to reconnect after 3 seconds
        setTimeout(connectToWebSocket, 3000);
      };

      ws.current.onerror = (error) => {
        console.error('WebSocket error:', error);
        setIsConnected(false);
      };
    } catch (error) {
      console.error('Failed to connect to WebSocket:', error);
      setIsConnected(false);
    }
  };

  const startMeeting = async () => {
    try {
      const response = await fetch(`${backendUrl}/api/meeting/start`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        setIsRecording(true);
        setPrompts([]);
        setSpeakerData([]);
        setMeetingDuration(0);
        console.log('Meeting started successfully');
      } else {
        console.error('Failed to start meeting');
        alert('Failed to start meeting. Please check if the backend is running.');
      }
    } catch (error) {
      console.error('Error starting meeting:', error);
      alert('Error starting meeting. Please check your connection.');
    }
  };

  const stopMeeting = async () => {
    try {
      const response = await fetch(`${backendUrl}/api/meeting/stop`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        setIsRecording(false);
        console.log('Meeting stopped successfully');
      } else {
        console.error('Failed to stop meeting');
      }
    } catch (error) {
      console.error('Error stopping meeting:', error);
    }
  };

  const setMeetingAgenda = async () => {
    if (!agenda.trim()) return;

    const items = agenda.split('\n').filter(item => item.trim()).map(item => item.trim());

    try {
      const response = await fetch(`${backendUrl}/api/meeting/agenda`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ items }),
      });

      if (response.ok) {
        setAgendaItems(items);
        console.log('Agenda set successfully');
      } else {
        console.error('Failed to set agenda');
      }
    } catch (error) {
      console.error('Error setting agenda:', error);
    }
  };

  const dismissPrompt = (index) => {
    setPrompts(prev => prev.filter((_, i) => i !== index));
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = Math.floor(seconds % 60);
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  // Prepare chart data
  const chartData = {
    labels: speakerData.map(speaker => speaker.speaker_id),
    datasets: [
      {
        label: 'Speaking Time (seconds)',
        data: speakerData.map(speaker => speaker.talk_time_in_seconds),
        backgroundColor: speakerData.map((_, index) => {
          const colors = ['#3B82F6', '#10B981', '#F59E0B', '#EF4444', '#8B5CF6'];
          return colors[index % colors.length];
        }),
        borderColor: speakerData.map((_, index) => {
          const colors = ['#2563EB', '#059669', '#D97706', '#DC2626', '#7C3AED'];
          return colors[index % colors.length];
        }),
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Real-Time Speaking Time Analysis',
        font: {
          size: 18,
        },
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Time (seconds)',
        },
      },
      x: {
        title: {
          display: true,
          text: 'Speakers',
        },
      },
    },
    animation: {
      duration: 300,
    },
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>🎤 Real-Time Meeting Assistant</h1>
        <div className="connection-status">
          <span className={`status-indicator ${isConnected ? 'connected' : 'disconnected'}`}>
            {isConnected ? '🟢 Connected' : '🔴 Disconnected'}
          </span>
        </div>
      </header>

      <main className="main-content">
        {/* Control Panel */}
        <div className="control-panel">
          <div className="meeting-controls">
            <button
              onClick={isRecording ? stopMeeting : startMeeting}
              className={`control-button ${isRecording ? 'stop' : 'start'}`}
              disabled={!isConnected}
            >
              {isRecording ? '⏹️ Stop Meeting' : '▶️ Start Meeting'}
            </button>
            <span className="meeting-duration">
              Duration: {formatTime(meetingDuration)}
            </span>
          </div>

          <div className="agenda-section">
            <h3>Meeting Agenda</h3>
            <textarea
              value={agenda}
              onChange={(e) => setAgenda(e.target.value)}
              placeholder="Enter agenda items (one per line)"
              className="agenda-input"
              rows="4"
            />
            <button onClick={setMeetingAgenda} className="set-agenda-button">
              Set Agenda
            </button>
            {agendaItems.length > 0 && (
              <div className="agenda-items">
                <h4>Current Agenda:</h4>
                <ul>
                  {agendaItems.map((item, index) => (
                    <li key={index}>{item}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        </div>

        {/* Prompts Section */}
        {prompts.length > 0 && (
          <div className="prompts-section">
            <h3>📋 Facilitator Prompts</h3>
            {prompts.map((prompt, index) => (
              <div key={index} className={`prompt ${prompt.prompt_type}`}>
                <div className="prompt-content">
                  <strong>{prompt.prompt_type.replace('_', ' ').toUpperCase()}:</strong>
                  <p>{prompt.message}</p>
                </div>
                <button
                  onClick={() => dismissPrompt(index)}
                  className="dismiss-button"
                >
                  ✕
                </button>
              </div>
            ))}
          </div>
        )}

        {/* Speaking Time Visualization */}
        <div className="chart-section">
          <div className="chart-container">
            {speakerData.length > 0 ? (
              <>
                <Bar data={chartData} options={chartOptions} />
                <div className="speaker-stats">
                  <h3>📊 Speaker Statistics</h3>
                  <div className="stats-grid">
                    {speakerData.map((speaker, index) => (
                      <div key={speaker.speaker_id} className="speaker-stat">
                        <div className="speaker-info">
                          <span className="speaker-name">{speaker.speaker_id}</span>
                          <span className="speaker-time">
                            {formatTime(speaker.talk_time_in_seconds)}
                          </span>
                          <span className="speaker-percentage">
                            ({speaker.percentage}%)
                          </span>
                        </div>
                        <div className="progress-bar">
                          <div
                            className="progress-fill"
                            style={{
                              width: `${speaker.percentage}%`,
                              backgroundColor: chartData.datasets[0].backgroundColor[index],
                            }}
                          />
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </>
            ) : (
              <div className="no-data">
                <h3>🎯 Ready to Analyze</h3>
                <p>
                  {isRecording
                    ? 'Listening for speakers... Start talking to see real-time analysis!'
                    : 'Click "Start Meeting" to begin real-time speech analysis.'}
                </p>
              </div>
            )}
          </div>
        </div>

        {/* Instructions */}
        <div className="instructions">
          <h3>📖 How it works</h3>
          <div className="feature-list">
            <div className="feature">
              <span className="feature-icon">🎤</span>
              <span>Real-time audio capture and transcription</span>
            </div>
            <div className="feature">
              <span className="feature-icon">👥</span>
              <span>Automatic speaker identification</span>
            </div>
            <div className="feature">
              <span className="feature-icon">⚖️</span>
              <span>Participation balance monitoring (60% rule)</span>
            </div>
            <div className="feature">
              <span className="feature-icon">⏰</span>
              <span>Agenda progress tracking (10-minute alerts)</span>
            </div>
            <div className="feature">
              <span className="feature-icon">🤫</span>
              <span>Silence detection (30-second alerts)</span>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
